import numpy as np
import matplotlib.pyplot as plt

map = 3
block = []
with open("../WindowsRelease/maps/{}.txt".format(map), 'r') as f:
    s = f.readlines()
    for i in range(100):
        for j in range(100):
            if s[i][j] == '#':
                x = j/2
                y = (99-i)/2
                size = 0.5
                block.append([x+size/2, y+size/2])
    block = np.array(block)
    
def draw(data):
    color = ['r', 'y', 'g', 'b']
    for i in range(3):
        #plt.scatter(data[i][:, 0], data[i][:, 1], color=color[i], marker='.')
        plt.scatter(data[i][-1:, 0], data[i][-1:, 1], color=color[i], marker='o')
    plt.show()
